INSERT INTO summaries VALUES (249,'A strange spider  from  the  depths  of  a  jungle  is  accidentally  transported  back  to  the  good  old  US  of  A.  Through  numerous  coincidences and accidents, it finds a  home  in  the  Doctors  new  home  (well  in  the  barn).  After  mating','Rob Hartill <Robert.Hartill@cm.cf.ac.uk>');
INSERT INTO summaries VALUES (136,'Angela Bennetts a software engineer type who works  from  home  and  has  few friends outside of cyberspace. Taking her first vacation in  years she becomes embroiled in a web (sic) of computer espionage.','Rob Hartill <Robert.Hartill@cm.cf.ac.uk>');
INSERT INTO summaries VALUES (124,'A  gay  lawyer stricken with AIDS seeks assistance from a homophobic  personal injury attorney when a prestigious  Philadelphia  law  firm  fires  him  for incompetence. Not the standard issue movie or even  the standard courtroom drama but a sensitive','Keith Loh <loh@sfu.ca>');
